import React, { useState} from 'react';
import seedrandom from 'seedrandom';


function PickPacks() {

    const [totalPacks, setTotalPacks] = useState(3);
    const [openPacks, setOpenPacks] = useState(12);
    const timestamp = new Date('2001-12-11').getTime();
    const [seed, setSeed] = useState(timestamp);
    const [results, setResults] = useState([]);

    const randomGeneratePacks = () => {
        const total = parseInt(totalPacks);
        const open = parseInt(openPacks);

        if (open > total || isNaN(total) || isNaN(open) || total <= 0 || open <= 0) {
            alert("请输入有效的包数");
            return;
        }

        const rng = seedrandom(seed.toString());
        const available = new Set(Array.from({ length: total }, (_, i) => i + 1));
        const selected = [];

        while (selected.length < open) {
            const index = Math.floor(rng() * available.size);
            const value = Array.from(available)[index];
            available.delete(value);
            selected.push(value);
        }

        selected.sort((a, b) => a - b);
        setResults(selected);
    };

    return (
        <div className='home-title'>
            <h1>随机开包</h1>
            <input type="number" value={totalPacks} onChange={(e) => setTotalPacks(e.target.value)} />
            <input type="number" value={openPacks} onChange={(e) => setOpenPacks(e.target.value)} />
            <button onClick={randomGeneratePacks}>开包</button>
        </div>
    );
}

export default PickPacks;
