import React, {useEffect, useState} from 'react';

function FirstSecond() {

    const [order, setOrder] = useState('');

    useEffect(() => {
        const saved = localStorage.getItem('firstsecond');
        if (saved) {
            const { order } = JSON.parse(saved);
            setOrder(order)
        }
    })

    const decideOrder = () => {
        const randomZeroOne = Math.floor(Math.random() * 2);
        var result = randomZeroOne === 1 ? '先攻' : '後攻';
        setOrder(result);
        localStorage.setItem('firstsecond', JSON.stringify({ order: result }));
    };

    return (
        <div className='container-fluid'>

            <div className='d-flex  align-items-center justify-content-center mx-4 flex-column'>

                <h1 className='mb-4'>先后攻</h1>
                {order && <h1 className='display-1'>{order}</h1>}
                <button className="btn btn-primary btn-lg col-3 mx-4" onClick={decideOrder}>决定</button>

            </div>
        </div>
    );
}

export default FirstSecond;
