import React, { useState } from 'react';
import './NavBar.css';

function NavBar() {
    const [menuOpen, setMenuOpen] = useState(false);

    return (
        <nav className="navbar">
        <div className="logo">WS工具箱</div>

        {/* ☰ 按钮只在小屏显示 */}
        <button className="menu-button" onClick={() => setMenuOpen(!menuOpen)}>
            ☰
        </button>

        {/* 菜单项：根据屏幕大小不同显示方式不同 */}
        <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
            <a href="/">主页</a>
            <a href="/pick_packs">选择开包</a>
            <a href="/first_second">先后攻</a>
            <a href="/dice">骰子</a>
            <a href="/chess_clock">棋钟</a>
            <a href="/shuffle">随机洗牌</a>
        </div>
        </nav>
    );
    }

export default NavBar;
