import React, { useState } from 'react';
import './NavBar.css';

function NavBar() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="navbar">
      <div className="logo">WS Tool Box</div>

      {/* ☰ 按钮只在小屏显示 */}
      <button className="menu-button" onClick={() => setMenuOpen(!menuOpen)}>
        ☰
      </button>

      {/* 菜单项：根据屏幕大小不同显示方式不同 */}
      <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
        <a href="/">Home</a>
        <a href="/pick_packs">Pick Packs</a>
        <a href="/first_second">First Second</a>
        <a href="/dice">Dice/骰子</a>
        <a href="/chess_clock">Chess Clock/棋钟</a>
        <a href="/shuffle">Shuffle/洗牌</a>
        <a href="/contact">Contact/联系</a>
      </div>
    </nav>
  );
}

export default NavBar;
